from unittest import TestCase
from ChatApp.utils.find_primitive_root import find_primitive, is_prime


class TestFindPrimitiveRoot(TestCase):
    def test_find_primitive_root(self):
        numbers = ""
        res_num = ""
        for i in range(3, 30000):
            if is_prime(i):
                res_num += str(find_primitive(i))
        self.assertEqual(numbers, res_num)
